mapper.select:
select * from board where

(userid like('%${keyword}%') or title like('%${keyword}%') or content like('%${keyword}%')) and
boardnum > 0 order by boardnum desc limit #{startrow}, #{amount}

select * from board where

(userid like('%${keyword}%') or title like('%${keyword}%') or content like('%${keyword}%')) and
boardnum > 0 order by likecnt desc limit #{startrow}, #{amount}

select * from board where

(userid like('%${keyword}%') or title like('%${keyword}%') or content like('%${keyword}%')) and
