mapper.update:
update package
set  visibility ='O'
where packagenum = #{packagenum}

mapper.select:

select * from package
where



startdate <= #{enddate}
and enddate >= #{startdate}
and maxcnt >= #{maxcnt}
and regionname = #{regionname}
and visibility = 'O'
and (
    startdate >= #{startdate} and enddate <= #{enddate}
)
