mapper.select:
select b.*
from board b
join (
    SELECT boardnum
    FROM board_like
    WHERE userid = #{userid}
    ORDER BY boardnum DESC
    LIMIT #{cri.startrow}, #{cri.amount}
) bl ON b.boardnum = bl.boardnum

select *
from (select * from package where startdate > curdate()) p
join (
    SELECT packagenum
    FROM reservation
    WHERE userid = #{userid}
    ORDER BY packagenum DESC
) r ON p.packagenum = r.packagenum order by p.packagenum desc limit #{cri.startrow}, #{cri.amount}

select *
from (select * from package where startdate < curdate()) p
join (
    SELECT packagenum
    FROM reservation
    WHERE userid = #{userid}
    ORDER BY packagenum DESC
) r ON p.packagenum = r.packagenum order by p.packagenum desc limit #{cri.startrow}, #{cri.amount}

select * from review 
where guidenum = (select guidenum from guide where userid = #{userid}) 
order by packagenum limit #{cri.startrow}, #{cri.amount}
